<?php if (!defined('BASEPATH')) { exit('No direct script access allowed'); } class Orcamentos extends MY_Controller { public function __construct() { parent::__construct(); $this->load->model('orcamentos_model'); $this->data['menuOrcamentos'] = 'Orcamentos'; } public function index() { $this->gerenciar(); } public function gerenciar() { if (!$this->permission->checkPermission($this->session->userdata('permissao'), 'vOrcamento')) { $this->session->set_flashdata('error', 'Você não tem permissão para visualizar orçamentos.'); redirect(base_url()); } $this->load->library('pagination'); $this->data['configuration']['base_url'] = site_url('orcamentos/gerenciar/'); $this->data['configuration']['total_rows'] = $this->orcamentos_model->count('orcamentos'); $this->pagination->initialize($this->data['configuration']); $this->data['results'] = $this->orcamentos_model->get('orcamentos', '*', '', $this->data['configuration']['per_page'], $this->uri->segment(3)); $this->data['view'] = 'orcamentos/orcamentos'; return $this->layout(); } public function adicionar() { if (!$this->permission->checkPermission($this->session->userdata('permissao'), 'aOrcamento')) { $this->session->set_flashdata('error', 'Você não tem permissão para adicionar orçamentos.'); redirect(base_url()); } $this->load->library('form_validation'); $this->data['custom_error'] = ''; if ($this->form_validation->run('orcamentos') == false) { $this->data['custom_error'] = (validation_errors() ? '<div class="form_error">' . validation_errors() . '</div>' : false); } else { $data = [ 'data' => $this->input->post('data'), 'validade' => $this->input->post('validade'), 'clientes_id' => $this->input->post('clientes_id'), 'descricao' => $this->input->post('descricao'), 'valor' => $this->input->post('valor'), 'status' => $this->input->post('status'), 'usuarios_id' => $this->session->userdata('id_admin'), ]; if ($this->orcamentos_model->add('orcamentos', $data) == true) { $this->session->set_flashdata('success', 'Orçamento adicionado com sucesso!'); log_info('Adicionou um orçamento'); redirect(site_url('orcamentos/adicionar/')); } else { $this->data['custom_error'] = '<div class="form_error"><p>Ocorreu um erro.</p></div>'; } } $this->data['view'] = 'orcamentos/adicionarOrcamento'; return $this->layout(); } public function editar() { if (!$this->uri->segment(3) || !is_numeric($this->uri->segment(3))) { $this->session->set_flashdata('error', 'Item não pode ser encontrado, parâmetro não foi passado corretamente.'); redirect('axcell'); } if (!$this->permission->checkPermission($this->session->userdata('permissao'), 'eOrcamento')) { $this->session->set_flashdata('error', 'Você não tem permissão para editar orçamentos.'); redirect(base_url()); } $this->load->library('form_validation'); $this->data['custom_error'] = ''; if ($this->form_validation->run('orcamentos') == false) { $this->data['custom_error'] = (validation_errors() ? '<div class="form_error">' . validation_errors() . '</div>' : false); } else { $data = [ 'data' => $this->input->post('data'), 'validade' => $this->input->post('validade'), 'clientes_id' => $this->input->post('clientes_id'), 'descricao' => $this->input->post('descricao'), 'valor' => $this->input->post('valor'), 'status' => $this->input->post('status'), 'usuarios_id' => $this->session->userdata('id_admin'), ]; if ($this->orcamentos_model->edit('orcamentos', $data, 'idOrcamento', $this->input->post('idOrcamento')) == true) { $this->session->set_flashdata('success', 'Orçamento editado com sucesso!'); log_info('Alterou um orçamento. ID: ' . $this->input->post('idOrcamento')); redirect(site_url('orcamentos/editar/') . $this->input->post('idOrcamento')); } else { $this->data['custom_error'] = '<div class="form_error"><p>Ocorreu um erro</p></div>'; } } $this->data['result'] = $this->orcamentos_model->getById($this->uri->segment(3)); $this->data['view'] = 'orcamentos/editarOrcamento'; return $this->layout(); } public function excluir() { if (!$this->permission->checkPermission($this->session->userdata('permissao'), 'dOrcamento')) { $this->session->set_flashdata('error', 'Você não tem permissão para excluir orçamentos.'); redirect(base_url()); } $id = $this->input->post('id'); if ($id == null) { $this->session->set_flashdata('error', 'Erro ao tentar excluir orçamento.'); redirect(base_url() . 'index.php/orcamentos/gerenciar/'); } $this->orcamentos_model->delete('orcamentos', 'idOrcamento', $id); log_info('Removeu um orçamento. ID: ' . $id); $this->session->set_flashdata('success', 'Orçamento excluido com sucesso!'); redirect(site_url('orcamentos/gerenciar/')); } }

