<?php if (!defined("BASEPATH")) { exit("No direct script access allowed"); } class Notificacoes_model extends CI_Model { public function __construct() { parent::__construct(); } public function get($table, $fields, $where = "", $perpage = 0, $start = 0, $one = false, $array = "array") { $this->db->select($fields); $this->db->from($table); $this->db->limit($perpage, $start); if ($where) { $this->db->where($where); } $this->db->order_by("data_envio", "desc"); $query = $this->db->get(); $result = !$one ? $query->result() : $query->row(); return $result; } public function getById($id) { $this->db->where("idNotificacao", $id); $this->db->limit(1); return $this->db->get("notificacoes")->row(); } public function add($table, $data) { return $this->db->insert($table, $data); } public function edit($table, $data, $fieldID, $ID) { $this->db->where($fieldID, $ID); $this->db->update($table, $data); if ($this->db->affected_rows() >= 0) { return true; } return false; } public function delete($table, $fieldID, $ID) { $this->db->where($fieldID, $ID); $this->db->delete($table); if ($this->db->affected_rows() == "1") { return true; } return false; } public function count($table) { return $this->db->count_all($table); } }

